// The verbose output settings.
static BOOL g_bShowVerbose = FALSE ;

/*//////////////////////////////////////////////////////////////////////
// EXTERNALLY VISIBLE CODE STARTS HERE
//////////////////////////////////////////////////////////////////////*/

void SetVerbose ( BOOL bShow )
{
   g_bShowVerbose = bShow ;
}

void VerboseOut ( LPCTSTR szFmt , ... )
{
   if ( TRUE == g_bShowVerbose )
   {
      va_list marker ;

      va_start ( marker, szFmt ) ;

      _vtprintf ( szFmt , marker ) ;

      va_end( marker );
   }
}

void WarningOut ( LPCTSTR szFmt , ... )
{
   va_list marker ;

   va_start ( marker, szFmt ) ;

   _vftprintf ( stderr , szFmt , marker ) ;

   va_end( marker );

}

